# GTX 1080 Ti コンテナ起動スクリプト (Windows PowerShell)
# docker compose の GPU パススルー問題を回避するため docker run を直接使用

$IMAGE = "frtggroup/fx-ea-gtx:latest"
$NAME  = "ea"
$CREDS = ""

# 既存コンテナを停止・削除
$existing = docker ps -a --format "{{.Names}}" 2>$null | Where-Object { $_ -eq $NAME }
if ($existing) {
    Write-Host "[*] 既存コンテナを停止中..."
    docker stop $NAME 2>$null
    docker rm   $NAME 2>$null
}

Write-Host "[*] コンテナ起動 (GPU: --gpus all)..."
docker run -d `
    --name $NAME `
    --gpus all `
    -p 8080:8080 `
    -p 2222:22 `
    --shm-size 2gb `
    --restart unless-stopped `
    -e NVIDIA_VISIBLE_DEVICES=all `
    -e NVIDIA_DRIVER_CAPABILITIES=compute,utility `
    -e H100_MODE=0 `
    -e DASHBOARD_PORT=8080 `
    -e MAX_PARALLEL=2 `
    -e VRAM_PER_TRIAL=5 `
    -e NODE_ID=gtx1080ti `
    -e GDRIVE_FOLDER_ID=1RhIqnpyzQrSuEgIAD7KroNWfELdElS6L `
    -e "GDRIVE_CREDENTIALS_BASE64=$CREDS" `
    -e BT_CAPITAL=150000 `
    -e BT_LEVERAGE=1000 `
    -e BT_RISK_PCT=1.0 `
    -e DATA_URL=https://fxea.s3.isk01.sakurastorage.jp/USDJPY_M1.csv `
    -e DATA_PATH=/workspace/data/USDJPY_M1.csv `
    $IMAGE

Write-Host ""
Write-Host "[OK] コンテナ起動完了"
Write-Host "     ダッシュボード : http://localhost:8080"
Write-Host "     ログ確認       : docker logs -f $NAME"
Write-Host "     停止           : docker stop $NAME"
